import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

class Square extends React.Component {
    render() {
        return (
            <button className="square">
                {/* TODO */}
            </button>
        );
    }
}

class Board extends React.Component {
    renderSquare(i) {
        return <Square />;
    }

    render() {
        const status = 'Next player: X';

        return (
            <div>
                <div className="status">{status}</div>
                <div className="board-row">
                    {this.renderSquare(0)}
                    {this.renderSquare(1)}
                    {this.renderSquare(2)}
                </div>
                <div className="board-row">
                    {this.renderSquare(3)}
                    {this.renderSquare(4)}
                    {this.renderSquare(5)}
                </div>
                <div className="board-row">
                    {this.renderSquare(6)}
                    {this.renderSquare(7)}
                    {this.renderSquare(8)}
                </div>
            </div>
        );
    }
}

class Game extends React.Component {
    render() {
        return (
            <div className="game">
                <div className="game-board">
                    <Board />
                </div>
                <div className="game-info">
                    <div>{/* status */}</div>
                    <ol>{/* TODO */}</ol>
                </div>
            </div>
        );
    }
}

function suma(a,b) {
    return a + b;
}

class Converter extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            a : document.getElementById("element").innerHTML,
            b : 0,
            result : 0
        }
    }

    render() {
        let result = 10;
        return (
            <div>
                <form method="post">
                    <h2>Login</h2>
                    <input type="text" id="a" placeholder="Frecuencia" required="required" />
                    <input type="text" id="b" placeholder="Distancia" required="required" />
                    <button onClick={suma(1+2)}></button>
                    <p>{result}</p>
                </form>
            </div>
        );
    }
}

// ========================================

ReactDOM.render(
    <Converter />,
    document.getElementById('root')
);
