import React from "react";
import ReactDOM from "react-dom";
import "./index.css";


// Controles
class ValueInputA extends React.Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e) {
    this.props.onValueChange(e.target.value);
  }

  render() {    
    return (
      <fieldset>
        <legend>Ingrese el valor de A:</legend>
        <input value={this.props.value} onChange={this.handleChange}/>
      </fieldset>
    );
  }
}

class ValueInputB extends React.Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e) {
    this.props.onValueChange(e.target.value);
  }

  render() {
      for(let i = 0; i < 4; i++){
        return (
          addB(this.props.name, this.props.clase, this.props.value, this.handleChange)
          );
      }
  }
}

function addB (name, clase, value, handleChange) {
  return (
    <li>
      <legend>Ingrese el valor de {name}:</legend>
      <input value={value} class={clase} onChange={handleChange}/>
    </li>
  );
}

// Operaciones
function ResultadoSuma(props) {
  return (
    <p>Hola mundo {props.result}</p>
  );
}

function trySum(valA, valB) {
  return (valA + valB).toString();
}



// Main
class Calculator extends React.Component {
  constructor(props) {
    super(props);

    // Variables de usuario
    this.state = {
      a : '10', b : 0,
       _Frecuencia : 0,
       _Distancia :0,
       _Gtx : 0,
       _Ptx : 0,
       _Pctx : 0,
       _Pcotx : 0

    };

    // Inicio de los controladores de eventos
    this.handleValueChangeA = this.handleValueChangeA.bind(this);
    this.handleValueChangeB = this.handleValueChangeB.bind(this);

    this.handleFrecChange = this.handleFrec.bind(this);
    this.handleDistChange = this.handleDist.bind(this);
    this.handleGtxChange = this.handleGtx.bind(this);
    this.handlePtxChange = this.handlePtx.bind(this);
    this.handlePctxChange = this.handlePctx.bind(this);
    this.handlePcotxChange = this.handlePcotx.bind(this);
  }

  // Controladores de eventos
  handleValueChangeA = (value) => this.setState({a: value});
  handleValueChangeB = (value) => this.setState({b: value});

  handleFrecChange = (value) => this.setState({_Frecuencia : value});
  handleDistChange = (value) => this.setState({_Distancia : value});
  handleGtxChange = (value) => this.setState({_Gtx : value});
  handlePtxChange = (value) => this.setState({_Ptx : value});
  handlePctxChange = (value) => this.setState({_Pctx : value});
  handlePcotxChange = (value) => this.setState({_Pcotx : value});

  render() {
    // Constantes
    const a = this.state.a;
    const b = this.state.b;

    // Operaciones
    const result = a + ' , ' + b;

    return (
      <div>
        <fieldset>
            <ValueInputB 
              value = {b}
              name = 'a'
              clase = 'txt'
              onValueChange={this.handleValueChangeB}
            />
            <ResultadoSuma result={result}/>
        </fieldset>
      </div>
    );
  }
}

ReactDOM.render(<Calculator />, document.getElementById("root"));
