import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

// Global
let visibleArray = [0, 1, 1, 1, 1, 1];

// Controles
class ValueInputA extends React.Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e) {
    this.props.onValueChange(e.target.value);
  }

  render() {
    return (
      <fieldset>
        <legend>Ingrese el valor de A:</legend>
        <input value={this.props.value} onChange={this.handleChange} />
      </fieldset>
    );
  }
}

class CreateInput extends React.Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange = (e) => this.props.onValueChange(e.target.value);

  render() {
    const visible = (this.props.isVisible === 1) ? 'txt-input' : 'txt-input-hidden';

    return (
      <div>
        <legend>{this.props.name}:</legend>
        <input value={this.props.value} class={visible} onChange={this.handleChange} />
      </div>
    );
  }
}

// Operaciones
function SetForm (type) {
  switch (type) {
    case 'FSL':
      alert('FSL seleccionado');
      visibleArray = [1,1,0,0,0,0];
      break;
    case 'PIRE':
      alert('PIRE seleccionado');
      visibleArray = [0,0,1,1,1,1];
      break;
    default :
      visibleArray = [1,1,1,1,1,1];
      break;
  }
}

// Main
class Calculator extends React.Component {
  constructor(props) {
    super(props);

    // Variables de usuario
    this.state = {
      a: '10', b: 0,
      _Frecuencia: 0,
      _Distancia: 0,
      _Gtx: 0,
      _Ptx: 0,
      _Pctx: 0,
      _Pcotx: 0

    };

    // Inicio de los controladores de eventos
    this.handleValueChangeA = this.handleValueChangeA.bind(this);
    this.handleValueChangeB = this.handleValueChangeB.bind(this);

    this.handleFrecChange = this.handleFrecChange.bind(this);
    this.handleDistChange = this.handleDistChange.bind(this);
    this.handleGtxChange = this.handleGtxChange.bind(this);
    this.handlePtxChange = this.handlePtxChange.bind(this);
    this.handlePctxChange = this.handlePctxChange.bind(this);
    this.handlePcotxChange = this.handlePcotxChange.bind(this);

  }

  // Controladores de eventos
  handleValueChangeA = (value) => this.setState({ a: value });
  handleValueChangeB = (value) => this.setState({ b: value });

  handleFrecChange = (value) => this.setState({ _Frecuencia: value });
  handleDistChange = (value) => this.setState({ _Distancia: value });
  handleGtxChange = (value) => this.setState({ _Gtx: value });
  handlePtxChange = (value) => this.setState({ _Ptx: value });
  handlePctxChange = (value) => this.setState({ _Pctx: value });
  handlePcotxChange = (value) => this.setState({ _Pcotx: value });

  setFSL = () => visibleArray = [1,1,0,0,0,0];
  setPIRE = () => visibleArray = [0,0,1,1,1,1];


  render() {
    // Constantes
    const a = this.state.a;
    const b = this.state.b;
    const showProperty = visibleArray;

    const _Frecuencia = this.state._Frecuencia;
    const _Distancia = this.state._Distancia;
    const _Gtx = this.state._Gtx;
    const _Ptx = this.state._Ptx;
    const _Pctx = this.state._Pctx;
    const _Pcotx = this.state._Pcotx;

    // Operaciones
    const result = a + ' , ' + b;

    return (
      <div>
        <fieldset>
          <CreateInput name='Frecuencia' value={_Frecuencia}
            isVisible={showProperty[0]} onValueChange={this.handleFrecChange} />

          <CreateInput name='Distancia' value={_Distancia} 
            isVisible={showProperty[1]} onValueChange={this.handleDistChange} />

          <CreateInput name='GTx' value={_Gtx}
            isVisible={showProperty[2]} onValueChange={this.handleGtxChange} />

          <CreateInput name='Ptx' value={_Ptx} 
            isVisible={showProperty[3]} onValueChange={this.handlePtxChange} />

          <CreateInput name='Pctx' value={_Pctx}
            isVisible={showProperty[4]} onValueChange={this.handlePctxChange} />

          <CreateInput name='Pcotx' value={_Pcotx} 
            isVisible={showProperty[5]} onValueChange={this.handlePcotxChange} />

          <p>{result}</p>

          <button onClick={() => this.setFSL()}>FSL</button>
          <button onClick={() => this.setPIRE()}>PIRE</button>
          <button onClick={() => this.render()}>render</button>


        </fieldset>
      </div>
    );
  }
}

ReactDOM.render(<Calculator />, document.getElementById("root"));
