import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

class Square extends React.Component {
    render() {
        return (
            <button className="square">
                {/* TODO */}
            </button>
        );
    }
}

class Board extends React.Component {
    renderSquare(i) {
        return <Square />;
    }

    render() {
        const status = 'Next player: X';

        return (
            <div>
                <div className="status">{status}</div>
                <div className="board-row">
                    {this.renderSquare(0)}
                    {this.renderSquare(1)}
                    {this.renderSquare(2)}
                </div>
                <div className="board-row">
                    {this.renderSquare(3)}
                    {this.renderSquare(4)}
                    {this.renderSquare(5)}
                </div>
                <div className="board-row">
                    {this.renderSquare(6)}
                    {this.renderSquare(7)}
                    {this.renderSquare(8)}
                </div>
            </div>
        );
    }
}

class Game extends React.Component {
    render() {
        return (
            <div className="game">
                <div className="game-board">
                    <Board />
                </div>
                <div className="game-info">
                    <div>{/* status */}</div>
                    <ol>{/* TODO */}</ol>
                </div>
            </div>
        );
    }
}

function suma() {
    alert('sumando');
}

class Converter extends React.Component {
    render() {
        return (
            <div>
                <form>
                    N√∫mero 1: <input id="num1" type="number" />
                    N√∫mero 2: <input id="num2" type="number" />
                    <p id="resultado"></p>
                    <br /><br />
                    <input type="button" value="SUMAR" onclick="suma(); return false;" /><br />
                    <input type="button" value="RESTAR" onclick="operaciones('restar'); return false;" /><br />
                    <input type="button" value="MULTIPLICAR" onclick="operaciones('multiplicar'); return false;" /><br />
                    <input type="button" value="DIVIDIR" onclick="operaciones('dividir'); return false;" />
                </form>
            </div>
        );
    }
}

// ========================================

