import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

const scaleNames = {
  c: "Celsius",
  f: "Fahrenheit",
};

function toCelsius(fahrenheit) {
  return ((fahrenheit - 32) * 5) / 9;
}

function toFahrenheit(celsius) {
  return (celsius * 9) / 5 + 32;
}

function tryConvert(temperature, convert) {
  const input = parseFloat(temperature);
  if (Number.isNaN(input)) {
    return "";
  }
  const output = convert(input);
  const rounded = Math.round(output * 1000) / 1000;
  return rounded.toString();
}

function BoilingVerdict(props) {
  if (props.celsius >= 100) {
    return <p>The water would boil.</p>;
  }
  return <p>The water would not boil.</p>;
}

class TemperatureInput extends React.Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e) {
    this.props.onTemperatureChange(e.target.value);
  }

  render() {
    const temperature = this.props.temperature;
    const scale = this.props.scale;
    return (
      <fieldset>
        <legend>Enter temperature in {scaleNames[scale]}:</legend>
        <input value={temperature} onChange={this.handleChange} />
      </fieldset>
    );
  }
}

class ValueInputA extends React.Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e) {
    this.props.onValueChange(e.target.value);
  }

  render() {    
    return (
      <fieldset>
        <legend>Ingrese el valor de A:</legend>
        <input value={this.props.value} onChange={this.handleChange}/>
      </fieldset>
    );
  }
}

class ValueInputB extends React.Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e) {
    this.props.onValueChange(e.target.value);
  }

  render() {
    return (
      <fieldset>
        <legend>Ingrese el valor de B:</legend>
        <input value={this.props.value} onChange={this.handleChange}/>
      </fieldset>
    );
  }
}

function ResultadoSuma(props) {
  return (
    <p>Hola mundo {props.result}</p>
  );
}

function trySum(valA, valB) {
  return (valA + valB).toString();
}




class Calculator extends React.Component {
  constructor(props) {
    super(props);
    this.handleCelsiusChange = this.handleCelsiusChange.bind(this);
    this.handleFahrenheitChange = this.handleFahrenheitChange.bind(this);
    // Variables
    this.state = { temperature: "", scale: "c", variable: "", newValue: "", result : 0, a : '10', b : 0};
    //Handlers
    this.handleValueChangeA = this.handleValueChangeA.bind(this);
    this.handleValueChangeB = this.handleValueChangeB.bind(this);
  }

  //Handlers
  handleCelsiusChange(temperature) {
    this.setState({ scale: "c", temperature });
  }

  handleFahrenheitChange(temperature) {
    this.setState({ scale: "f", temperature });
  }

  handleValueChangeA(newValue) {
    this.setState({a: newValue });
  }

  handleValueChangeB(newValue) {
    this.setState({ b: newValue});
  }

  render() {
    const scale = this.state.scale;
    const temperature = this.state.temperature;
    const celsius = scale === "f" ? tryConvert(temperature, toCelsius) : temperature;
    const fahrenheit = scale === "c" ? tryConvert(temperature, toFahrenheit) : temperature;

    // COnstantes
    const a = this.state.a;
    const b = this.state.b;

    // Operaciones
    const result = a + ' , ' + b;



    return (
      <div>
        <TemperatureInput
          scale="c"
          temperature={celsius}
          onTemperatureChange={this.handleCelsiusChange}
        />
        <TemperatureInput
          scale="f"
          temperature={fahrenheit}
          onTemperatureChange={this.handleFahrenheitChange}
        />

        <BoilingVerdict celsius={parseFloat(celsius)} />

        <ValueInputA 
          value = {a}
          onValueChange={this.handleValueChangeA}
        />
        <ValueInputB 
          value = {b}
          onValueChange={this.handleValueChangeB}
        />

        <ResultadoSuma result={result}/>
      </div>
    );
  }
}

ReactDOM.render(<Calculator />, document.getElementById("root"));
